surface.CreateFont( "AFont_PickupH",{	font = "Russo One",	size = ScreenScaleEx(22),	weight = 100,	antialias = true})surface.CreateFont( "AFont_PickupH_blur",{	font = "Russo One",	size = ScreenScaleEx(22),	weight = 100,	blursize = 3})m_tPickupHistory = {}function DrawPickupHistory()	local x = ScrW()/2	local y = ScrH()/1.3	local w, h = surface.GetTextSizeEx("A","AFont_PickupH")	for k,v in ipairs( m_tPickupHistory ) do		local i = k - 1		if v.fade < CurTime() then			v.alpha = v.alpha - 5			v.alpha = math.Clamp( v.alpha, 0, 255 )		end		local m_iAlphaInverted = -v.alpha + 255		if v.alpha ~= 0 then			draw.DrawAbdulText( "You picked up a "..ClassToFormat( v.class ), "AFont_PickupH", "AFont_PickupH_blur", x, y - h*i, Color(255,255,255,v.alpha), 1, 0, m_iAlphaInverted )		else			table.remove(m_tPickupHistory,k)		end	endendhook.Add("HUDPaint","PICKUPHISTORY.LIB.HOOK",DrawPickupHistory)net.Receive( "abdul_net.PlayerPickupItem", function( length )	local classname = net.ReadString()	local weapon = { class = classname, fade = CurTime() + 1.5, alpha = 255 }	if #m_tPickupHistory == 5 then		table.remove(m_tPickupHistory,1)	end	table.insert( m_tPickupHistory, weapon )		hook.Run("MLG.fx.OnPickupItem", weapon)end)